    private fun extractMangaData(html: String): JsonObject? {
        // Pattern: "mangaData":{"id":553,"name":"Fighting Ward",...}
        val pattern = """"mangaData":\{([^}]+(?:\{[^}]*\}[^}]*)*)\}""".toRegex()
        val match = pattern.find(html) ?: return null

