    fun toSChapter(): SChapter = SChapter.create().apply {
        name = chapter
        date_upload = MangaSwat.apiDateFormat.tryParse(createdAt)
        url = "/chapters/$id/$slug/"
    }
}

@Serializable
internal class PageListResponse(
    internal val images: List<PageDto>,
